mapper.select:
select sum(xsje) as xsje,sum(xsjetq) as xsjetq, TO_CHAR(ROUND(decode(sum(xsjetq), 0, 0, (sum(xsje) - sum(xsjetq)) / sum(xsjetq)) * 100,2), 'fm9999990.0999') || '%' as xszz, sum(jybs) as jybs,sum(jybstq) as jybstq, TO_CHAR(ROUND(decode(sum(jybstq), 0, 0, (sum(jybs) - sum(jybstq)) / sum(jybstq)) * 100, 2), 'fm9999990.0999') || '%' as jybsZz, sum(kll) as kll,sum(kllTq) as kllTq, TO_CHAR(ROUND(decode(sum(kllTq), 0, 0, (sum(kll) - sum(kllTq)) / sum(kllTq)) * 100, 2), 'fm9999990.0999') || '%' as kllZz, round(decode(sum(jybs), 0, 0, sum(xsje) * 10000 / sum(jybs)), 2) as kdj, round(decode(sum(jybstq), 0, 0, sum(xsjetq) * 10000 / sum(jybstq)), 2) as kdjTq, TO_CHAR(ROUND(decode(round(decode(sum(jybstq), 0, 0, sum(xsjetq) * 10000 / sum(jybstq)), 2), 0, 0, (round(decode(sum(jybs), 0, 0, sum(xsje) * 10000 / sum(jybs)), 2) - round(decode(sum(jybstq), 0, 0, sum(xsjetq) * 10000 / sum(jybstq)), 2)) / round(decode(sum(jybstq), 0, 0, sum(xsjetq) * 10000 / sum(jybstq)), 2)) * 100, 2), 'fm9999990.0999') || '%' as kdjZz from ( select lp.shopId as shopId, lp.shopname as shopName, lp.bj_yt as shopYt, decode(nvl(b.xsje, 0), 0, 0, 1) as kbType, a.xsje, b.xsje as xsjeTq, a.xsbs as jybs, b.xsbs as jybsTq, a.kll, b.kll as kllTq from lp_bi_shop lp left join (select shopId, shopName, sum(xsje) as xsje, sum(xsbs) as xsbs, sum(kll) as kll from ZH_MD_TBZS where rq between to_date('${startTime}', 'yyyymmdd') and to_date('${endTime}', 'yyyymmdd') group by shopId, shopName) a ON lp.shopid = a.shopid LEFT JOIN (select shopId, sum(xsje) as xsje, sum(xsbs) as xsbs, sum(kll) as kll, round(decode(sum(xsbs), 0, 0, sum(xsje) * 10000 / sum(xsbs)), 2) as kdj from ZH_MD_TBZS where rq between to_date('${startTimedb}', 'yyyymmdd') and to_date('${endTimedb}', 'yyyymmdd') group by shopId) b ON a.shopId = b.shopId Where lp.bj_bd = 0
AND lp.shopid IN (${shopId})
)
